Create database LinesDB

-- Para ativar o banco de dados 
Use LinesDB

SELECT * FROM Linhas
SELECT * FROM Estacoes
SELECT * FROM Coordenadas

-- Criação da tabela linhas
CREATE TABLE Linhas (
    ID INT PRIMARY KEY,
    Nome VARCHAR(50),
);

-- Criação da tabela Estacoes
Create Table Estacoes(
	ID INT PRIMARY KEY,
    Nome VARCHAR(50),
	LinhasID INT FOREIGN KEY REFERENCES Linhas(ID), 
);

-- Criação da tabela Coordenadas
Create Table Coordenadas(
	ID INT PRIMARY KEY,
	EstacaoID INT FOREIGN KEY REFERENCES Estacoes(ID), 
	Latitude FLOAT, -- ou DECIMAL/NUMERICO USA-SE "FLOAT" --
	Longitude FLOAT, 
);



